jQuery(document).ready(function() {
    switch (document.URL.slice(-2)) {
        case 'ru':
            localStorage.setItem('language', 'ru');
            resetText(0);
            break;
        case 'kz':
            localStorage.setItem('language', 'kz');
            resetText(1);
            break;
        case 'en':
            localStorage.setItem('language', 'en');
            resetText(2);
            break;
    }

    if (localStorage.getItem('language') == 'kz') {
        resetText(1);
    } else if (localStorage.getItem('language') == 'ru') {
        resetText(0);
    } else if (localStorage.getItem('language') == 'en') {
        resetText(2);
    }


    $('.language').click(function() {
        switch (this.id) {
            case "ru":
                console.log('ru');
                localStorage.setItem('language', 'ru');
                resetText(0);
                break;
            case 'kz':
                console.log('kz');
                localStorage.setItem('language', 'kz');
                resetText(1);
                break;
            case 'en':
                console.log('en');
                localStorage.setItem('language', 'en');
                resetText(2);
                break;
        }
    });
});

function resetText(id) {
    $('.lang').each(function() {
        try {
            $('#' + this.id).html(res[$('#' + this.id).attr('key')][id]);
        } catch (e) {
            console.log('Слово не переведена, забыл поставить id к div', $(this))
        }
    });

    res.lang = id;
}

